<template>  <div class="home">    <p v-for="(item,index) in buttonArray" @click="goNewPage(item.routing,index)" :key="index">{{item.name}}</p>  </div></template><script>  export default {    name: 'home',    data() {      return {        buttonArray: [          {            name: '跳转1',            routing: 'Goods'          },          {            name: '跳转2',            routing: 'About'          },          {            name: '跳转3',            routing: 'Goods'          }        ]      }    },    methods: {      goNewPage(router, params) {        //路由跳转        this.$router.push({path: `/${router}/${params}`})      }    }  }</script><style lang="scss">  @import "../style/pxToRem.scss";  .home {    display: flex;    flex-direction: row;    justify-content: space-between;    width: pxToRem(750);    height: pxToRem(200);    font-size: pxToRem(18);    font-weight: bolder;    p {      width: pxToRem(200);      height: pxToRem(200);      line-height: pxToRem(200);      background-color: cadetblue;      &:active {        background-color: darkolivegreen;      }    }  }</style>